import "./App.css";
import Select from "react-select";
import { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [data, setDatas] = useState([]);
  const [userSelect, setUserSelect] = useState("")
  const [isShow, setIsShow] = useState(true)

  const getBerries = async () => {
    const berries = await fetch('https://pokeapi.co/api/v2/berry/')
    const value = await berries.json();
    const result = value.results.map(data => {
      return {
        value: data.name,
        label: data.name
      }
    })
    setDatas(result.sort((a, b) => a.label.localeCompare(b.label)))
  }
  useEffect(() => {
    getBerries()
  }, [])

  const handleSubmit = () => {
    setIsShow(true)
  }

  const handleChange = () => {
    setUserSelect(value)
  }

  return (
    <div className="App">
      <button onClick={handleSubmit} onChange={handleChange}></button>
      <Select options={data} />
    </div>
  );
}

export default App;
